                        "__cdata": "a:18:{i:1582639591910;s:12:\"Introduction\";i:1582639592335;s:16:\"Customer Service\";i:1582639592687;s:30:\"Original Customer Service Page\";i:1582639593015;s:14:\"Case Selection\";i:1582639593333;s:43:\"Exemption from the License Sanction Process\";i:1582639593702;s:15:\"Notice to Payor\";i:1582639594039;s:27:\"License Sanction Conference\";i:1582639594373;s:22:\"Holding the Conference\";i:1582639594725;s:18:\"Payment Agreements\";i:1582639595053;s:38:\"Results of License Sanction Conference\";i:1582639595368;s:28:\"Certificate of Noncompliance\";i:1582639595685;s:84:\"Continuing License Sanction on Cases when Pursuing IA-Initiated Interstate Referral \";i:1582639596021;s:52:\"Notice of Withdrawal of Certificate of Noncompliance\";i:1582639596310;s:22:\"District Court Hearing\";i:1582639596645;s:35:\"Ending the License Sanction Process\";i:1582639596917;s:102:\"Reviewing CASESTAT: When a Customer Payment is Received in the Office or when Conducting a Case Review\";i:1582639597254;s:46:\"Payments Received at CSRU - Checking CASESTAT \";i:1582639597597;s:27:\"Frequently Asked Questions \";}"
