                        "__cdata": "a:15:{i:1586783584466;s:23:\"Contacts for Bankruptcy\";i:1586783584730;s:20:\"Administrative Levy \";i:1586783584965;s:16:\"Credit Reporting\";i:1586783585230;s:25:\"License Sanction (LISAN) \";i:1586783585488;s:24:\"Income Withholding (IWO)\";i:1586783585746;s:42:\"How Bankruptcy Affects Medical Enforcement\";i:1586783586057;s:23:\"State Offset (STT/DOP) \";i:1586783586271;s:24:\"Federal Offset (FED/FAO)\";i:1586783586537;s:8:\"Location\";i:1586783586793;s:30:\"Bankruptcy Chapter Information\";i:1586783587052;s:25:\"Bankruptcy Calendar Flags\";i:1586783587309;s:21:\"Bankruptcy Narratives\";i:1586783587560;s:22:\"Bankruptcy Terminology\";i:1586783587832;s:19:\"Proof of Claim Form\";i:1586783588098;s:9:\"Resources\";}"
