                        "__cdata": "a:21:{i:1586789557787;s:30:\"Confidential Information Form \";i:1586789558055;s:27:\"Docket Number Discrepancies\";i:1586789558288;s:48:\"Docket Number on Every Document is Not Necessary\";i:1586789558546;s:27:\"EDMS (eFlex) Best Practices\";i:1586789558804;s:25:\"Efiling for Other Offices\";i:1586789559086;s:21:\"Electronic Signatures\";i:1586789559336;s:11:\"Event Codes\";i:1586789559586;s:23:\"Exhibits Best Practices\";i:1586789559874;s:26:\"Income Withholding Orders \";i:1586789560165;s:28:\"Indexing or Linking to Cases\";i:1586789560421;s:25:\"Instructions for JB wi-fi\";i:1586789560671;s:36:\"Multi-Caption Filings Best Practices\";i:1586789560951;s:39:\"Notes Field on CSeF Efile Packet Screen\";i:1586789561225;s:11:\"Office PINs\";i:1586789561515;s:16:\"Proof of Service\";i:1586789561765;s:16:\"Record Retention\";i:1586789562022;s:9:\"Redaction\";i:1586789562265;s:30:\"Registration of Foreign Orders\";i:1586789562545;s:16:\"Rejected Filings\";i:1586789562795;s:37:\"Reliance on EDMS notification process\";i:1586789563037;s:11:\"SharePoint \";}"
