                        "__cdata": "a:19:{i:1586793735007;s:53:\"Allowable Health Insurance Deductions for Guidelines \";i:1586793735423;s:18:\"Overview of MEDSUM\";i:1586793735681;s:45:\"Overview of MEDSUM, MEDSUM2 & MEDSUM3 Screens\";i:1586793735899;s:21:\"MEDSUM Batch Programs\";i:1586793736141;s:24:\"When a Case is By-passed\";i:1586793736376;s:50:\"Determination of High Potential (and HI POT codes)\";i:1586793736595;s:19:\"Generation of Forms\";i:1586793736829;s:36:\"Selection of a Health Insurance plan\";i:1586793737063;s:57:\"Enforcement - Selecting a Health Insurance Plan Flowchart\";i:1586793737299;s:50:\"When the Payee must Select a Health Insurance Plan\";i:1586793737556;s:47:\"Contesting the Enforcement of Health Insurance \";i:1586793737798;s:30:\"Adding a MS obligation to ICAR\";i:1586793738054;s:114:\"Unclear Health Insurance Language - \"OTHER\" Field for Party Ordered to Provide Health Insurance on HIUPDATE Screen\";i:1586793738320;s:58:\"Health Insurance Flags that no longer issue as of 5/21/12 \";i:1586793738554;s:10:\"Flowcharts\";i:1586793738766;s:14:\"Best Practices\";i:1586793739016;s:24:\"Commonly Asked Questions\";i:1586793739250;s:9:\"Resources\";i:1586793739500;s:8:\"Appendix\";}"
